				local bg = Add.BG(torso)

				bg.cframe = ComputePos(torso.Position, mousepos)

				bg.P = 5500

				wait(0.4)

				bg:remove()

			end)

		end

		Attacks[Attack]()

	end

end



function KeyDown(key, mouse)

	key = key:lower()

	if Able then

		local aimplace = nil

		if Aim then

			aimplace = mouse.Hit.p

		end

		if key == string.char(48) then

			Notime(function()

				local k = nil

				repeat

					k = mouse.KeyUp:wait()

				until k == string.char(48)

				Block.Value = false

			end)

			Bloc()

		elseif key == "e" then

			if Block.Value == false and AbleAll then

				SpinSlash(aimplace)

			end

		elseif key == "r" then

			if Block.Value == false and AbleAll then

				Flawp(aimplace)

			end

		elseif key == "f" then

			if Block.Value == false and AbleAll then

				Raeg()

			end

		elseif key == "t" then

			Notime(function()

				local k = nil

				CounterKey = true

				repeat

					k = mouse.KeyUp:wait()

				until k == "t"

				CounterKey = false

			end)

		elseif key == "q" then

			if Block.Value == false and AbleAll then

				local a = nil

				if Aim then

					a = mouse.Hit.p

				end

				RageSlash(a)

			end

		end

	end

end



