						if(mouse.Target~=nil)then
							if(mouse.Target:IsA("BasePart"))and(mouse.Target~=iOrb.Orb)or(mouse.Target.Name~="cParticle")or(mouse.Target.Name~="cParticle2")then
								mouse.Target:remove()
